$primary-color: #4CAF50;
$text-color: #333;
$background-color: #fff;
$border-radius: 4px;
$transition-duration: 0.3s;

// Colors
$discount-color: #FF4D6D;
$hit-color: #A64DFF;
$hover-color: #A64DFF;

