// Form styles
.form-popup {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 1000;

  &.active {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  &__content {
    background-color: $background-color;
    padding: 2rem;
    border-radius: $border-radius;
    width: 90%;
    max-width: 500px;
    position: relative;
  }

  &__close {
    position: absolute;
    top: 1rem;
    right: 1rem;
    cursor: pointer;
    background: none;
    border: none;
    font-size: 1.5rem;
    color: $text-color;

    &:hover {
      color: #A64DFF;
    }
  }

  &__form {
    display: flex;
    flex-direction: column;
    gap: 1rem;
  }

  &__field {
    position: relative;
  }

  &__input {
    width: 100%;
    padding: 0.5rem;
    border: 1px solid #ccc;
    border-radius: $border-radius;

    &.error {
      border-color: red;
    }
  }

  &__error {
    color: red;
    font-size: 0.8rem;
    display: none;
    margin-top: 0.25rem;

    &.active {
      display: block;
    }
  }
}

// Utility classes
.desktop-only {
  @include mobile {
    display: none;
  }
}

.discount {
  background-color: red;
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: $border-radius;
}

.day-product {
  background-color: $primary-color;
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: $border-radius;
}

.hit {
  background-color: orange;
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: $border-radius;
}
